sys_variable_value.value:
(function execute(inputs, outputs) {
  var trackingNum = inputs.tracking_num;
  var delDateTime = new GlideDateTime();
  var outputStatus = 'Delivered';
  var randResult = getRandomInt(1, 100);

  if (randResult < 3) {
     outputStatus = 'delivery_exception';
     delDateTime.add(getRandomInt(1,2) * 86400000);
} else if (randResult < 50) {
     outputStatus = 'delivered';
     delDateTime.add(getRandomInt(0,1) * -86400000);
} else if (randResult < 90) {
     outputStatus = 'in_transit';
     delDateTime.add(getRandomInt(1,6) * 86400000);
} else {
     outputStatus = 'new';
     delDateTime.add(getRandomInt(5,14) * 86400000);
};


  outputs.status = outputStatus;
  outputs.expect_date = delDateTime;
})(inputs, outputs);


function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return parseInt(Math.floor(Math.random() * (max - min + 1)) + min);
};

